% CallModelComparison%% Call the ModelComparisonTutorial function with% various parameters to explore their effect.% Clear and closeclear all; close all% Basic parameter settheta = [1 3 -9 6 5]';nFit = 9;noiseSds = [0.001 .2 1 5];nX = 16;nReplications = 2;% Call and attach a score to each methodfor i = 1:length(noiseSds)	[nSplitCross,nBootCross,nSplitGen,nBootGen,nAIC] = ...		ModelComparisonTutorial(theta,nFit,noiseSds(i),nX,nReplications);	score(i,1) = sum(nSplitCross.*abs((1:nFit)-length(theta)))/sum(nSplitCross);	score(i,2) = sum(nBootCross.*abs((1:nFit)-length(theta)))/sum(nBootCross);	score(i,3) = sum(nSplitGen.*abs((1:nFit)-length(theta)))/sum(nSplitGen);	score(i,4) = sum(nBootGen.*abs((1:nFit)-length(theta)))/sum(nBootGen);    score(i,5) = sum(nAIC.*abs((1:nFit)-length(theta)))/sum(nAIC);    pauseend[nil,whichMethod] = min(score,[],2);whichMethod 